import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Header from './components/Header'

const UpdateStudentData = () => {
  let params = useParams();
  let getId = params.id.match(/\d+/);
  if(getId){

    const [data,setData] = useState([]);

    async function getStudentData(){
        let result = await fetch("http://127.0.0.1:8000/api/getDataStudent/"+getId);
        result = await result.json();
        setData(result);
        
    }
    console.warn(data)
    useEffect(()=>{
      getStudentData();
    },[])

  }else{

    null
    
  }

   
  return (
    <div>
        <Header />
        <div className='container'>
        <br></br>
            <h1 className='text-center'>Update Student Data</h1>
            <br></br>
            <div className='col-sm-6 offset-3'>
            <input type="text" placeholder='Your Name' className='form-control' />
            <br></br>
            <input type="email" placeholder='Your Email' className='form-control' />
            <br></br>
            <input type="text" placeholder='Your Phone Number' className='form-control' />
            <br></br>
            <div className="row">
                <div className="col">
                    <input type="file" className="form-control" placeholder="Your Image" aria-label="Your Image" />
                </div>
                <div className="col">
                <img style={{width:180}} src={"https://images.ctfassets.net/hrltx12pl8hq/3E5SSUuJCKt1KyebMAdr7f/6b98ce27789b03a6b4a62092ea4566b6/Group_5_B.jpg?fit=fill&w=2200&h=688&fm=webp"} />
                </div>
            </div>

            <button className='btn btn-success text-light'>Update</button>
            </div>
        </div>
    </div>
  )
}

export default UpdateStudentData
