import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Header from './components/Header'

const UpdateStudentData = () => {
  let params = useParams();
    let getId = params.id.match(/\d+/);
  const [data,setData] = useState([]);


    async function getStudentData(){
        let result = await fetch("http://127.0.0.1:8000/api/getDataStudent/"+getId);
        result = await result.json();
        setData(result);  
    }
    console.warn(data)
    useEffect(()=>{
      getStudentData();
    },[])
    
   
  return (
    <div>
        <Header />
        <div className='container'>
        <br></br>
            <h1 className='text-center'>Update Student Data</h1>
            <br></br>
            <div className='col-sm-6 offset-3'>
            <input type="text" defaultValue={data.name} placeholder='Your Name' className='form-control' />
            <br></br>
            <input type="email" defaultValue={data.email} placeholder='Your Email' className='form-control' />
            <br></br>
            <input type="text" defaultValue={data.phone} placeholder='Your Phone Number' className='form-control' />
            <br></br>
            <div className="row">
                <div className="col">
                    <input type="file" defaultValue={data.image_student} className="form-control" placeholder="Your Image" aria-label="Your Image" />
                </div>
                <div className="col">
                <img style={{width:120}} src={"http://127.0.0.1:8000/"+data.image_student} />
                </div>
            </div>
            <button className='btn btn-success text-light'>Update</button>
            </div>
        </div>
    </div>
  )
}

export default UpdateStudentData
