import React, { useEffect,useState } from 'react'
import Header from './components/Header'

const Login = () => {

  useEffect(()=>{
    if(localStorage.getItem('student-user-info'))
    history("/")
  },[]);

  const[email, setEmail] = useState("");
  const[password, setPassword] = useState("");

  return (
    <div>
        <Header />
        <div className="container">
            <br></br>
            <h1 className='text-center'>login</h1>
            <br></br>
            <div className='col-sm-6 offset-3'>
            <input type="email" onChange={(e)=>setEmail()} placeholder='User Name or Email' className='form-control' />
            <br></br>
            <input type="password" placeholder='Your Password' className='form-control' />
            <br></br>
            <button className='btn btn-success text-light'>Login</button>
            </div>
        </div>
    </div>
  )
}

export default Login
